mapper.select:
/* findMeetByDistance */ SELECT s.id, s.title, s.message, s.channel_url AS channelUrl, s.member_id AS memberId, m.name, s.reg_dt AS regDt, IFNULL(SUM(CC.commentCnt + CC.commentReplyCnt), 0) AS commentCnt, m.client_token AS clientToken, m.profile_url AS profileUrl, m.profile_url_org AS profileUrlOrg, m.profile_file AS profileFile, s.original_img AS originalImg, s.thumbnail_img AS thumbnailImg, s.file_name AS fileName, s.sortation, s.meet_start_dt AS meetStartDt, s.meet_end_dt AS meetEndDt, m.location_lat AS locationLat, m.location_lon AS locationLon, d.district_kr_name AS districtKrName, d.district_kr_name_abbr AS districtKrNameAbbr, s.member_limit AS memberLimit FROM meet s INNER JOIN member m ON s.member_id = m.id AND s.use_yn = 'Y' INNER JOIN district_code d ON m.district_code = d.district_code
AND ( CASE WHEN s.sortation = 'F' THEN (meet_start_dt >= ${meetStartDt} AND meet_start_dt <= ${meetEndDt}) ELSE ( meet_start_dt >= ${meetStartDt} AND meet_end_dt <= ${meetEndDt} ) END )
AND m.location_lat BETWEEN ${seLat} AND ${nwLat} AND m.location_lon BETWEEN ${nwLon} AND ${seLon}
LEFT JOIN ( SELECT * FROM ( SELECT s.id AS meetid, c.id AS comment_id, COUNT(c.id) AS commentCnt FROM meet s LEFT JOIN comment c ON s.id = c.meet_id AND c.sortation = 'M' GROUP BY s.id ) AA LEFT JOIN ( SELECT c.meet_id, c.id, COUNT(r.id) AS commentReplyCnt FROM comment c LEFT JOIN comment_reply r ON c.id = r.comment_id AND c.sortation = 'M' GROUP BY c.meet_id ) BB ON AA.meetid = BB.meet_id ) AS CC ON s.id = CC.meetid -- LEFT JOIN like_say l ON s.id = l.say_id GROUP BY s.id ORDER BY s.reg_dt DESC LIMIT ${page}, 20
