mapper.select:
SELECT s.id, s.message, s.member_id AS memberId, m.name, s.reg_dt AS regDt, IFNULL(SUM(AA.commentCnt + BB.commentReplyCnt), 0) AS commentCnt FROM say s INNER JOIN member m ON s.member_id = m.id AND s.use_yn = 'Y'
AND m.location_lat BETWEEN ${seLat} AND ${nwLat} AND m.location_lon BETWEEN ${nwLon} AND ${seLon}
LEFT JOIN (SELECT s.id AS say_id, c.id AS comment_id, COUNT(c.id) AS commentCnt FROM say s LEFT JOIN comment c ON s.id = c.say_id GROUP BY s.id) AS AA ON s.id = AA.say_id LEFT JOIN (SELECT c.say_id, c.id, COUNT(r.id) AS commentReplyCnt FROM comment c LEFT JOIN comment_reply r ON c.id = r.comment_id GROUP BY c.say_id) AS BB ON AA.comment_id = BB.id -- LEFT JOIN like_say l ON s.id = l.say_id GROUP BY s.id LIMIT ${page}, 20
